import React, { useState } from 'react'
import TodoForm from './TodoForm'
import { RiCloseCircleLine } from 'react-icons/ri'
import { TiEdit } from 'react-icons/ti'
import { AiOutlinePlusCircle } from 'react-icons/ai'
import { Link } from 'react-router-dom'
import Todostyles from './Todo.module.css'

function Todo({ todos, completeTodo, deleteTodo, updateTodo }) {
  const [update, setUpdate] = useState({
    id: null,
    value: '',
  })

  const submitUpdate = (value) => {
    updateTodo(update.id, value)
    setUpdate({
      id: null,
      value: '',
    })
  }

  if (update.id) {
    return <TodoForm edit={update} onSubmit={submitUpdate} />
  }

  return todos.map((todo, index) => (
    // <div
    //   className={todo.isComplete ? 'todo-row complete' : 'todo-row'}
    //   key={index}
    // >
    //   <div key={todo.id} onClick={() => completeTodo(todo.id)}>
    //    {todo.text}
    // </div>

    <div
      className={Todostyles.todo}
      style={{ textDecoration: todos.completed ? 'line-through' : '' }}
    >
      {todo.text}
      <button onClick={() => completeTodo(index)} className={Todostyles.button}>
        Complete
      </button>
      <div className={Todostyles.icons}>
        <RiCloseCircleLine
          onClick={() => deleteTodo(todo.id)}
          className={Todostyles.deleteIcon}
        />
        <TiEdit
          onClick={() => setUpdate({ id: todo.id, value: todo.text })}
          className={Todostyles.editIcon}
        />
        <Link to={`/items/${todo.id}`} key={todo.id}>
          <AiOutlinePlusCircle className={Todostyles.icon} />
        </Link>
      </div>
    </div>
  ))
}

export default Todo
